function GetSingleProductHtml(product){var baseImagePath=$("#product-base-img-path").val();product.imageName=$.ConvertImageNameToWebP(product.imageName);var baseProduct=product,finalPrice=baseProduct.price-(baseProduct.discountPrice>0?baseProduct.discountPrice:0),isRemaining=baseProduct.firstActiveSelectedOptionValue.quantityInStock>0;return` <div class="cart-item">\n                <div class="row d-flex align-items-center text-start text-md-center">\n                    <div class="col-12 col-md-5">\n                        <div class="d-flex align-items-center">\n                            <a href="${GetProductListDtoUrl(baseProduct)}" title="${baseProduct.title}">\n                                <img class="cart-item-img"\n                                     src="${baseImagePath+baseProduct.imageName}"\n                                     alt="${baseProduct.title}" title="${baseProduct.title}">\n                            </a>\n                            <div class="cart-title text-start">\n                                <a class="text-dark" href="${GetProductListDtoUrl(baseProduct)}"\n                                title="${baseProduct.title}">\n                                    <strong>${baseProduct.title}</strong>\n                                </a>\n                                <br>\n                            </div>\n                        </div>\n                    </div>\n                    <div class="col-12 col-md-7 mt-4 mt-md-0">\n                        <div class="row align-items-center">\n                            <div class="col-md-2">\n                                ${isRemaining?`<div class="row">\n    <div class="col-6 d-md-none text-muted">قیمت هر عدد</div>\n    <div class="col-6 col-md-12 text-end text-md-center">\n    ${finalPrice.toLocaleString()}</div>\n</div>`:""}\n                            </div>\n                            <div class="col-md-3">\n                                <div class="row">\n                                    <div class="col-6 d-md-none text-muted">وضعیت</div>\n                                    <div class="col-6 col-md-12 text-end text-md-start">\n                                        ${isRemaining?'<div class="badge p-lg-2 bg-primary">موجود</div>':'<div class="badge p-lg-2 bg-danger">نا موجود</div>'}\n                                    </div>\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n                </div>\n            </div>`}$(document).ready((async function(e){let favoriteProducts=JSON.parse(localStorage.getItem("favoriteProducts"))||[];(async()=>{for(const productId of favoriteProducts){const response=await Get(`/products/detail-json/${productId}`);if(response&&response.data){const html=GetSingleProductHtml(response.data);$(".favorite-products-container").append(html)}}})()}));